import React from 'react';
import API from '../utils/api';
import Search from '../components/Search';
//import "./style.css"

import DateFormat from 'dateformat'

class EmployeeTable extends React.Component {

  state = {
    sort: "",
    results: [],
    search: ""
  }
  componentDidMount() {
    API.ApiSearch()
    .then(res => {
      this.setState({
        results: res.data.results
      })
      console.log(this.state.results)
    }).catch(err => 
      console.log(err))
  }

  handleInputChange = (e) =>{
    if(e.target.name === 'search') {
      const searchOpt = e.target.value.toLowerCase();
      this.setState({
        search: searchOpt
      })
    }
  }

  sortLastName = () => {
    const sortEmployees = 
    this.state.results.sort((a,b) => {
      if (b.name.last > a.name.last) {
        return -1
     }
     if (a.name.last > b.name.last) {
       return 1
     }
     return 0;
    });
    if (this.state.sort === 'DESC') {
      sortEmployees.reverse();
      this.setState({sort: 'ASC'});
    } else {
      this.setState({sort: 'DESC' });
    }
      this.setState({ results: sortEmployees })
  }
render() {
  <div>
    <Search
      handleInputChange={this.handleInputChange}search={this.state.search} />
      <div className="table-responsive">
        <table className="table-striped table responsive text-center table-hover">
          <thead>
            <tr>
              <th>Image</th>
              <th>First Name</th>
              <th>Last Name</th>
              
              <th>Image</th>
              <th>Image</th>
            </tr>
          </thead>
        </table>
      </div>
  </div>
}
}
