import React, { useState } from 'react';

const Header =(props)=> {

    const [name, setName] = useState('Kendra')
// all the functions and state just track data and then load a bunch of stuff to inject into the html below//


    const handleClick =(evt)=>{
        if(name ==='Kendra') {
            setName('Frank')
        }
        
    }

    return(
   
        <div className="jumbotron jumbotron-fluid">
            <h1 className="display-3">Employee Directory</h1>
            <p className="lead">Search according to first or last name</p>

            <h2>{name}</h2>
            <button onClick={handleClick}>Click Me!</button>
        </div>
 
    )
}

export default Header;
